SELECT game.gameNo, game.gameName, game.gameImage, game.gamePrice, game.gameContent, game.gameContent, game.gameGenre, CONCAT('(',DATE_FORMAT(gameReleasedDate,'%Y'),')') as gameReleasedDate, game.gameCategory FROM game, rate WHERE game.gameNo = rate.gameNo AND game.gameGenre REGEXP( SELECT DISTINCT CONCAT(
#{item}
FROM genre) ORDER BY rate.gameScore DESC, game.gameReleasedDate DESC LIMIT 5

SELECT gameNo,gameName,gameImage,gameGenre,DATE_FORMAT(gameReleasedDate,'%Y-%m-%d') as gameReleasedDate
FROM game USE INDEX (game_genre)
WHERE gameGenre LIKE CONCAT('%',#{gameCategory},'%');

SELECT gameNo,gameName,gameImage,gameGenre,DATE_FORMAT(gameReleasedDate,'%Y-%m-%d') as gameReleasedDate
FROM game USE INDEX (game_name)
WHERE gameName LIKE CONCAT('%',#{search},'%');
